for (var n = 0; n < years.length; n++) {
    var res = [];
    for (j = 0; j < data[n].length; j++) {
        res.push({
            name: jdData[n][j],
            value: data[n][j]
        });

    }

    res.sort(function (a, b) {
        return b.value - a.value;
    }).slice(0, 6);

    res.sort(function (a, b) {
        return a.value - b.value;
    });


    var res1 = [];
    var res2 = [];
    for (t = 0; t < res.length; t++) {
        res1[t] = res[t].name;
        res2[t] = res[t].value;
    }
    option.options.push({
        title: {
            text: years[n] + '年'
        },
        yAxis: {
            data: res1,
        },
        series: [{
            data: res2
        }, {
            data: res2
        }]
    });
